import React, { useEffect, useState } from 'react';
import './TopNavBar.css';
import Links from '../Links/Links';
import { scrollToSection } from '../../App.js';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText';

const TopNavBar = () => {
  const [visible, setVisible] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleScroll = () => {
    if (window.scrollY > 500) {
      setVisible(true);
    } else {
      setVisible(false);
    }
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  return (
    <div>
      <nav className={`TopNavBar ${visible ? 'visible' : ''}`}>
        <h1>RYAN SCHMID</h1>
        <div className='TopNavBar-links'>
          <p className='nav-link' onClick={() => scrollToSection('Home')}>HOME</p>
          <p className='nav-link' onClick={() => scrollToSection('Experience')}>EXPERIENCE</p>
          <p className='nav-link' onClick={() => scrollToSection('Education')}>EDUCATION</p>
          <p className='nav-link' onClick={() => scrollToSection('Projects')}>PROJECTS</p>
          <p className='nav-link' onClick={() => scrollToSection('Contact')}>CONTACT</p>
          <Links />
        </div>
      </nav>
      {visible && (
        <IconButton
          className="menu-icon"
          color="inherit"
          onClick={toggleDrawer}
        >
          <MenuIcon />
        </IconButton>
      )}
      <Drawer
        anchor="right"
        open={drawerOpen}
        onClose={toggleDrawer}
      >
        <List>
          <ListItem button onClick={() => scrollToSection('Home')}>
            <ListItemText primary="HOME" />
          </ListItem>
          <ListItem button onClick={() => scrollToSection('Experience')}>
            <ListItemText primary="EXPERIENCE" />
          </ListItem>
          <ListItem button onClick={() => scrollToSection('Education')}>
            <ListItemText primary="EDUCATION" />
          </ListItem>
          <ListItem button onClick={() => scrollToSection('Projects')}>
            <ListItemText primary="PROJECTS" />
          </ListItem>
          <ListItem button onClick={() => scrollToSection('Contact')}>
            <ListItemText primary="CONTACT" />
          </ListItem>
        </List>
      </Drawer>
    </div>
  );
};

export default TopNavBar;
