import React, { useEffect } from 'react';
import { useModal } from '../../ModalContext.js';
import Button from '../../mui_components/Button.js';
import './Modal.css';

const Modal = () => {
  const { isModalOpen, modalContent, closeModal } = useModal();

  useEffect(() => {
    if (isModalOpen) {
      // Forcing a reflow before adding the active class for transition
      const timer = setTimeout(() => {
        const modalOverlay = document.querySelector('.modal-overlay');
        modalOverlay.classList.add('active');
      }, 0);

      return () => clearTimeout(timer);
    }
  }, [isModalOpen]);

  const handleCloseModal = () => {
    const modalOverlay = document.querySelector('.modal-overlay');
    modalOverlay.classList.remove('active');

    setTimeout(() => {
      closeModal();
    }, 300); // Same duration as the transition

    // Remove the active class after the transition
    modalOverlay.addEventListener('transitionend', () => {
      modalOverlay.classList.remove('active');
    }, { once: true });
  };

  return (
    <div className='modal-overlay' onClick={handleCloseModal}>
      <div className={`modal-content ${isModalOpen ? 'active' : ''}`} onClick={(e) => e.stopPropagation()}>
        <span className='close' onClick={handleCloseModal}>
          &times;
        </span>
        {/* Display modal content */}
        {modalContent}
        <Button onClick={closeModal}>Close</Button>
      </div>
    </div>
  );
};

export default Modal;
