import './Home.css';
import { Parallax } from "react-parallax";
import Links from '../../components/Links/Links';
import '../../index.css';

// const bkg_pic_light = require("./home-bkg-light.png");
// const bkg_pic_dark = require("./home-bkg-dark.png");
const bkg_pic_lehigh = require("./home-bkg-lehigh.jpg");
const my_headshot = require("./home-my-headshot.jpg");

function Home(props) {
  // const lightStyles = getComputedStyle(document.documentElement);
  // const darkStyles = getComputedStyle(document.body);
  // const lightThemeBackground = lightStyles.getPropertyValue('--background');
  // const darkThemeBackground = darkStyles.getPropertyValue('--background');
  // for (let i = 0; i < lightStyles.length; i++) {
  //   const property = lightStyles[i];
  //   if (property.startsWith("--")) {
  //     console.log(lightStyles[i]);
  //   }
  // }
  const lightThemeBackground = 'white';
  const darkThemeBackground = 'black';

  let insideStyles = {
    padding: 0,
    position: "absolute",
    top: "50%",
    left: "50%",
    transform: "translate(-50%,-50%)",
    backgroundColor: lightThemeBackground
  };
  let bkgPicStyle = {
    height: "580px",
    backgroundColor: "rgba(255,255,255,0.4)"
  };
  if(props.theme === 'dark') {
    bkgPicStyle = {
      height: "580px",
      backgroundColor: "rgba(0,0,0,0.4)"
    };
    insideStyles = {
      padding: 0,
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%,-50%)",
      backgroundColor: darkThemeBackground
    };
  }
  return (
    <div>
      <div className="Home-nav">
        <h1 className="Home-nav-title">RYAN SCHMID</h1>
        <div className="Home-nav-list">
          <div className='Home-nav-accent' />
          <div className="Home-nav-links">
            <a href='#Home'>HOME</a>
            <a href='#Home'>EXPERIENCE</a>
            <a href='#Home'>EDUCATION</a>
            <a href='#Home'>PROJECTS</a>
            <a href='#Home'>CONTACT</a>
          </div>
          <div className='Home-nav-accent' />
        </div>
      </div> 
      <Parallax
        // bgImage={props.theme === "light" ? bkg_pic_light : bkg_pic_dark}
        bgImage={bkg_pic_lehigh}
        bgImageAlt="Background image"
        strength={200}
      >
        <div className='Home-bkg' style={bkgPicStyle}>
          <div className='Home-bkg-overlay' style={insideStyles}>
            <div className='Home-center'>
              <img className='Home-headshot' src={my_headshot} alt='Ryan Schmid'/>
              <div className='Home-center-text'>
                <p className="Home-welcome">
                  Welcome
                </p>
                <p className="Home-intro">
                  I'm Ryan, a recent Master's graduate of Lehigh University
                </p>
                <Links justify='left' />
              </div>
            </div>
          </div>
        </div>
      </Parallax>
    </div>
  );
}

export default Home;
