import React, { useEffect, useRef } from 'react';
import { useModal } from '../../ModalContext.js';
import './Card.css';

function Card(props) {
  const { openModal } = useModal();
  const descriptionRef = useRef(null);

  useEffect(() => {
    const element = descriptionRef.current;
    if (element) {
      const maxHeight = element.offsetHeight;
      let upperBound = element.scrollHeight;
      let lowerBound = 0;

      // Perform binary search to find the number of lines that fit
      while (upperBound - lowerBound > 1) {
        const mid = Math.floor((upperBound + lowerBound) / 2);
        element.style.height = mid + 'px';
        const height = element.offsetHeight;

        if (height <= maxHeight) {
          lowerBound = mid;
        } else {
          upperBound = mid;
        }
      }

      element.style.height = lowerBound + 'px';
    }
  }, [props.description]);

  const handleCardClick = () => {
    const modalContent = (
      <div>
        {/* Customize modal content based on the card */}
        <h3>{props.title}</h3>
        {props.children}
      </div>
    );
    openModal(modalContent);
  };

  return (
    <div className='Card' onClick={handleCardClick}>
      <div className='Card-img-wrapper'>
        <img className='Card-img' src={props.image} alt={props.alt} />
      </div>
      <div className='Card-content'>
        <h3>{props.title}</h3>
        <h4>{props.subtitle}</h4>
        <p ref={descriptionRef} className='Card-description'>{props.description}</p>
      </div>
    </div>
  );
}

export default Card;
