import './Card.css';
import React, { useState } from 'react';
import { Modal, Backdrop, Fade, Button } from '@mui/material';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

function Card(props) {
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div className='Card' onClick={handleOpen}>
      {/* Your existing card content */}
      <div className='Card-img-wrapper'>
        <img className='Card-img' src={props.image} alt={props.alt} />
      </div>
      <div className='Card-content'>
        <h3>{props.title}</h3>
        <h4>{props.subtitle}</h4>
        <p className='Card-description'>{props.description}</p>
      </div>

      {/* Modal */}
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby='modal-title'
        aria-describedby='modal-description'
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className='modal'>
            {/* Add your HTML content related to the card here */}
            <h2 id='modal-title'>{props.title}</h2>
            {/* Add other information related to the card */}
            <p id='modal-description'>{/* Your content here */}</p>
            <Button onClick={handleClose}>Close</Button>
          </div>
        </Fade>
      </Modal>
    </div>
  );
}

export default Card;
