import './App.css';
import useLocalStorage from 'use-local-storage';
import Home from './pages/Home/Home.js';
import About from './pages/About/About.js';
import Experience from './pages/Experience/Experience.js';
import Education from './pages/Education/Education.js';
import Projects from './pages/Projects/Projects.js';
import Button from './mui_components/Button.js';
import LightModeIcon from '@mui/icons-material/LightMode';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import TopNavBar from './components/TopNavBar/TopNavBar';

export function scrollToSection(sectionId) {
  const section = document.getElementById(sectionId);
  if (section) {
    const navBarHeight = getComputedStyle(document.documentElement).getPropertyValue('--top-nav-height');
    const offset = navBarHeight;

    // const sectionRect = section.getBoundingClientRect();
    // const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

    // window.scrollTo({
    //   top: sectionRect.top + scrollTop - offset,
    //   behavior: 'smooth',
    // });
    section.scrollIntoView({
      behavior: 'smooth',
      block: 'start',
      inline: 'nearest',
      offsetTop: -offset,
    });
  }
}

function App() {
  const defaultDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
  const [theme, setTheme] = useLocalStorage('theme', defaultDark ? 'dark' : 'light');

  const switchTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
  }

  return (
    <div className="App" data-theme={theme}>
      <TopNavBar />
      <Button className="theme-btn" onClick={switchTheme}>{theme === 'light' ? <DarkModeIcon/> : <LightModeIcon/>}</Button>
      {/* <h1>RYAN SCHMID</h1> */}
      <Home theme={theme} />
      {/* <Parallax bgImage={image1} strength={500}>
        <div style={{ height: 500 }}>
          <div style={insideStyles}>HTML inside the parallax</div>
        </div>
      </Parallax> */}
      <About/>
      <Experience/>
      <Education/>
      <Projects/>
      <About/>
      <About/>
      <About/>


    </div>
  )
}

export default App;
